class Particle {
  constructor() {
    this.pos = createVector(random(width), random(height));
    this.vel = createVector(0,0);
    this.acc = createVector(0,0);
    this.prev = this.pos.copy();
  }

  show() {
    strokeWeight(1);
    stroke(140, 210, 250, 255);
    line(this.prev.x, this.prev.y, this.pos.x, this.pos.y);
  }

  update(maxSpeed) {
    if (this.pos.x > width) {
        this.pos.x = 0;
    }
    if (this.pos.x < 0) {
        this.pos.x = width;
    }
    if (this.pos.y > height) {
        this.pos.y = 0;
    }
    if (this.pos.y < 0) {
        this.pos.y = height;
    }
    this.prev = this.pos.copy();
    this.vel.add(this.acc);
    this.vel.limit(maxSpeed);
    this.pos.add(this.vel);
    this.acc.set(0, 0);
  }

  follow(flowfield, scl, cols) {
    let x = floor(this.pos.x / scl);
    let y = floor(this.pos.y / scl);
    let index = x + y * cols;
    let force = flowfield[index];
    this.applyForce(force);
  }

  applyForce(force) {
    this.acc.add(force);
  }
}

function setup() {
  createCanvas(400, 400);
}

function draw() {
  background(255);

  let W;
  let num = 20;
  let mycolor1 = "red";
  let mycolor2 = "blue";
  let i = 0;

  W = width / num;

  while (i < num) {
    let xpos = i * W;

    if (i % 2 == 0 && mouseX > xpos && mouseX < xpos + W) {
      fill(mycolor1); // fill first half red
    } else if (i % 2 != 0 && mouseX > xpos && mouseX < xpos + W) {
      fill(mycolor2); // fill second half blue
    } else {
      fill(255); // when not hovering, white
    }

    rect(xpos, 0, W, height); // draw rect
    i++; // increment
  }
}

