// ------------------- Classes -------------------

// Drop class (animated falling shapes)
function Drop() {
  this.x = random(0, windowWidth);
  this.y = random(0, -windowHeight);

  this.show = function () {
    stroke(hue2B, sat2, br2);
    strokeWeight(0.5);
    fill(hue2B, sat2, br2);
    ellipse(this.x, this.y, random(5, 10), random(5, 10));
  };

  this.update = function () {
    this.speed = mappedAmp / 4;
    this.gravity = 1.05;
    this.y += this.speed * this.gravity;

    if (this.y > windowHeight) {
      this.y = random(0, -windowHeight);
      this.gravity = 0;
    }
  };
}

// Ball class (bouncing balls)
class Ball {
  constructor(xpos, ypos, w, dy, limit) {
    this.xpos = xpos;
    this.ypos = ypos;
    this.w = w;
    this.dy = dy;
    this.limit = limit;
    this.colorOffset = random(240, 350); // Random offset for hue, relative to hue2
  }

  getColor() {
    // Compute color dynamically based on current hue2 and the random offset
    return (hue2 + this.colorOffset) % 360;
  }

  draw() {
    this.move();
    noStroke();
    fill(this.getColor(), 100, mappedAmp * 1.5, 10);
    circle(this.xpos, this.ypos, this.w);
  }

  move() {
    if (this.ypos + this.w / 2 >= this.limit || this.ypos - this.w / 2 <= -20) {
      this.dy = -this.dy;
    }
    this.ypos += this.dy;
  }
}

