// ------------------- Setup Function -------------------
function setup() {
  // Create canvas and initialize color mode
  createCanvas(windowWidth, windowHeight);
  colorMode(HSB, 360, 100, 100, 100);

  // Create film grain effect and start the song
  createFilmGrain(0, 0, windowWidth, windowHeight, 500, 3);
