    for (let j = 0; j < n; j++) {
      let name = trackNames[i] + " " + j;
      let key = assignedKeys[keyIndex % assignedKeys.length];
      keyIndex++;
      loops[i][j] = new Loop("loops/" + trackNames[i] + j + ".mp3", i, j, name, key, visualizer);
    }

    for (let j = 0; j < n; j++) {
      for (let k = 0; k < n; k++) {
        if (j !== k) {
          loops[i][j].siblings.push(loops[i][k]);
        }
      }
    }
  }
}


