function stateDetected(error, results) {
  if (error) {
    console.log("stateDetected error:", error);
    return;
  } else {
    //populate global vars
    //noise values
    let _noise = 0;
    let _loose = 0;
    let _muscle = 0;
    let _jaw = 0;
    let _blink = 0;

    //both noise and focus
    let _eye = 0;

    //brain states
    let _clear = 0;
    let _focus = 0;
    let _dream = 0;

    //loop through results
    for (let i = 0; i < results.length; i++) {
      let result = results[i];
      let label = result.label;
      let confidence = result.confidence.toFixed(4);

      //populate vars if they have signifant value

      switch (label) {
        case "n":
          _noise = confidence;
          break;
        case "l":
          _loose = confidence;
          break;
        case "m":
          _muscle = confidence;
          break;
        case "b":
          _blink = confidence;
          break;
        case "e":
          _eye = confidence;
          break;
        case "j":
          _jaw = confidence;
          break;
        case "f":
          _focus = confidence;
          break;
        case "c":
          _clear = confidence;
          break;
        case "d":
          _dream = confidence;
          break;
        default:
        //console.log(`Unknown ${label}.`);
      }
    }

    //concat some values

    //noise
    if (_noise < _loose) {
      _noise = _loose;
    }
    state.noise = _noise;

    //muscle tension
    if (_muscle < _jaw) {
      _muscle = _jaw;
    }
    if (_muscle < _blink) {
      _muscle = _blink;
    }

    state.muscle = _muscle;

    if (_focus < _eye) {
      _focus = _eye;
    }

    state.focus = _focus;
    state.clear = _clear;
