    height / 2 - 50,
    "magenta",
    "better",
    sound6
  );
  platform7 = createPlatform(
    width / 2 + 200,
    height / 2 + 50,
    "lime",
    "faster",
    sound7
  );
  platform8 = createPlatform(
    width / 2 + 100,
    height / 2 + 150,
    "red",
    "stronger",
    sound8
