    "make it",
    sound2
  );
  platform3 = createPlatform(
    width / 2 - 200,
    height / 2 + 50,
    "lime",
    "do it",
    sound3
  );
  platform4 = createPlatform(
    width / 2 - 100,
    height / 2 + 150,
    "red",
    "makes us",
    sound4
  );

  // Right side platforms
  platform5 = createPlatform(
    width / 2 + 400,
    height / 2 - 150,
    "yellow",
    "harder",
    sound5
  );
  platform6 = createPlatform(
    width / 2 + 300,
    height / 2 - 50,
    "magenta",
    "better",
    sound6
  );
  platform7 = createPlatform(
    width / 2 + 200,
    height / 2 + 50,
    "lime",
    "faster",
    sound7
  );
  platform8 = createPlatform(
    width / 2 + 100,
    height / 2 + 150,
    "red",
    "stronger",
    sound8
  );
