var drop = [];

function Drop() {
  this.x = random(0, windowWidth);
  this.y = random(0, -windowHeight);

  this.show = function () {
    stroke(hue2B, sat2, 230);
    noStroke();
    strokeWeight(0.5);
    fill(hue2B, sat2, 230);
    ellipse(this.x, this.y, random(5, 10), random(5, 10));
  };
  this.update = function () {
    this.speed = amp / 5;
    this.gravity = 1.05;
    this.y = this.y + this.speed * this.gravity;

    if (this.y > windowHeight) {
      this.y = random(0, -windowHeight);
      this.gravity = 0;
    }
  };
}

let bballs = [];

class Ball {
  constructor(xpos, ypos, w, dy, limit) {
    this.xpos = xpos;
    this.ypos = ypos;
    this.w = w;
    this.dy = dy;
    this.color = random(abs(60 - hue2), hue2);
    this.limit = limit;
  }

  draw() {
    this.move(this.dy);

    noStroke();
    fill(this.color, 100, amp, 10);
    circle(this.xpos, this.ypos, this.w);
  }

  move() {
    if (this.ypos + this.w / 2 >= this.limit || this.ypos - this.w / 2 <= -20) {
      this.dy = -this.dy;
    }
    this.ypos += this.dy;
  }
}

