function wavePoints(H1, H2, S, B) {
  let waveArray = fft.waveform();

  for (let i = 0; i < wavefWidth; i++) {
    let waveIndex = floor(map(i, 0, wavefWidth, 0, waveArray.length));

    x = i;
    y = waveArray[waveIndex] * 200 + windowHeight / 2;

    noStroke();
