  frameRate(FR);
  hue1 = map(rot1, 0, 23, 0, 360);
  hue2 = map(rot2, 0, 23, 0, 360);
  background(hue1, sat1, br1, 40); // Transparent background with alpha

  // Generate hue variants for visual effects
  hue2A = (hue2 + 240) % 360;
  hue2B = (hue2 + 290) % 360;
  hue2C = (hue2 + 330) % 360;

  // Draw waveform and points
  myWave(hue2B, sat2, br2);
