  Hval1 = map(pot1, 0, 100, 0, 360);
  Hval2 = map(pot2, 0, 100, 0, 360);
  ranH1 = random((Hval1 + 0) % 360, (Hval1 + 50) % 360);
  ranH2 = random((Hval2 + 0) % 360, (Hval2 + 50) % 360);
}

// Draw ball effect
