// ------------------- Ripple Class -------------------

class Ripple {
  constructor(x, y, hue) {
    this.x = x;
    this.y = y;
    this.hue = hue;
    this.size = 0;
    this.opacity = 255;
  }

  draw() {
    this.size += 10;
    this.opacity -= 5;
    noFill();
    strokeWeight(2);
    stroke(this.hue, 100, 100, this.opacity);
    circle(this.x, this.y, this.size);
    circle(this.x, this.y, this.size * 0.75);
    circle(this.x, this.y, this.size * 0.5);
  }

  isFinished() {
    return this.opacity <= 0;
  }
}

