// Initialize or reinitialize the raindrops
function initializeDrops() {
  drops = [];
  let numDrops = floor((width * height) / 4000); // Adjust drop count based on canvas area
  for (let i = 0; i < numDrops; i++) {
    drops.push(new Drop());
